.scrollbar-mini {
  &::-webkit-scrollbar {
    width: 8px;
    height: 8px;
    overflow: overlay;
  }

  &::-webkit-scrollbar-thumb {
    border-radius: 8px;
    border: 2px solid transparent;
    background-clip: content-box;
    background-color: #d8d8d8;

    &:hover {
      background-color: #bebebe;
    }
  }

  &::-webkit-scrollbar-track {
    border-radius: 4px;
    background: #f9f9f9;
  }
}

.font-code {
  font-family: 'Operator Mono', 'Source Code Pro', Menlo, Monaco, Consolas, 'Courier New', monospace;
}

.font-emoji {
  font-family: "Segoe UI Emoji", "SF Pro SC", "SF Pro Text", "SF Pro Icons", "PingFang SC", "Helvetica Neue", "Helvetica", "Arial", sans-serif;
}

.css-arrow {
  border: solid currentColor;
  border-width: 0 2px 2px 0;
  display: inline-block;
  padding: 3px;

  &.right {
    transform: rotate(-45deg);
  }

  &.left {
    transform: rotate(135deg);
  }

  &.up {
    transform: rotate(-135deg);
  }

  &.down {
    transform: rotate(45deg);
  }
}

children.snippet:
@keyframes rotating180 {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(180deg);
  }
}

@keyframes rotating360 {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes zoomIn {
  0% {
    transform: scale(1);
  }

  90% {
    transform: scale(5);
    opacity: 1;
  }

  95% {
    opacity: 0;
    transform: scale(5);
  }

  96% {
    transform: scale(1);
  }

  97% {
    opacity: 0;
  }

  100% {
    opacity: 1;
  }
}

@keyframes textFadeInUp {
  0% {
    opacity: 0;
    transform: translate3d(0, 25px, 0);
  }

  100% {
    opacity: 1;
    transform: translate3d(0, 0, 0);
  }
}

@keyframes airShrinkIn {
  0% {
    opacity: 0;
    transform: scaleX(0.96);
  }

  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes airShrinkInReverse {
  0% {
    opacity: 0;
    transform: scale(1.12);
  }

  100% {
    opacity: 1;
    transform: scaleX(1);
  }
}

