demoPluginTpl:
const { addPlugin, copy, computed } = window.$qlUtils
addPlugin({
  label: '⚠️ Demo Plugin ⚠️',
  props: {
    onClick() {
      copy('Hello world!', true)
    }
  }
})
addPlugin((valRef) => {
  return {
    label: '⚠️ Demo Plugin (isStaticPlugin) ⚠️',
    children: [
      {
        label: '📋 Copy Input Value',
        props: {
          onClick: async () => {
            await copy(valRef.value, true)
          },
        },
      },
    ],
  }
}, {
  // If this parameter is passed in, the plug-in can be static (displayed directly in the list without entering text)
  isStaticPlugin: true,
})
addPlugin((valRef) => {
  return {
    label: '⚠️ Demo Plugin (Computed) ⚠️',
    children: () => {
      return computed(() => {
        return [{
          html: `Computed Value: <b style="color: red;">${valRef.value}</b>`
        }]
      })
    },
  }
})
