style:
.product-card {
  width: 335px;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(4px);
  border-radius: 20px;
  transition: all 0.3s;
  position: relative;
  display: flex;
  align-items: center;
  flex-direction: column;
  padding-bottom: 20px;
  margin: 100px auto;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.342);

  &:hover {
    background: #FFFFFF;
    box-shadow: 0 12px 52px 0 rgba(0, 0, 0, 0.06);
    border-radius: 20px;
  }

  .product-image {
    width: 120px;
    height: 120px;
    margin-top: -20px;
    margin-bottom: 20px;

    img {
      width: 100%;
      height: 100%;
      border-radius: 50%;
    }
  }

  .product-title {
    font-size: 16px;
    margin-bottom: 20px;

    b {
      font-weight: bold;
      color: #f44336;
    }
  }

  .btn-learn-more {
    border-radius: 28px;
    font-size: 16px;
    color: #f44336;
    font-weight: bold;
    text-align: center;
    padding: 8px 54px;
    border: 1px solid #f44336;
    text-decoration: none;
  }
}
